import React, { useState, useEffect } from 'react';
import { LeaderboardEntry } from '../../types';

interface LeaderboardProps {}

const Leaderboard: React.FC<LeaderboardProps> = () => {
  const [leaderboard, setLeaderboard] = useState<LeaderboardEntry[]>([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState<'all' | 'weekly' | 'monthly'>('all');
  const [lastUpdated, setLastUpdated] = useState<Date>(new Date());

  useEffect(() => {
    fetchLeaderboard();
  }, [filter]);

  // Real-time updates - poll every 10 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      fetchLeaderboard(true); // silent update
    }, 10000);

    return () => clearInterval(interval);
  }, [filter]);

  const fetchLeaderboard = async (silent = false) => {
    if (!silent) setLoading(true);
    try {
      const response = await fetch(`http://localhost:8080/api/leaderboard?period=${filter}`);
      const data = await response.json();

      if (data.success) {
        setLeaderboard(data.data || []);
        setLastUpdated(new Date());
      }
    } catch (error) {
      console.error('Error fetching leaderboard:', error);
    } finally {
      if (!silent) setLoading(false);
    }
  };

  const getRankIcon = (position: number) => {
    switch (position) {
      case 1: return 'ðŸ¥‡';
      case 2: return 'ðŸ¥ˆ';
      case 3: return 'ðŸ¥‰';
      default: return `#${position}`;
    }
  };

  const getRankFromElo = (elo: number): { name: string; color: string; icon: string } => {
    if (elo >= 2000) return { name: 'RADIANT', color: 'text-yellow-300', icon: 'âš¡' };
    if (elo >= 1800) return { name: 'IMMORTAL', color: 'text-purple-400', icon: 'ðŸ’Ž' };
    if (elo >= 1600) return { name: 'DIAMOND', color: 'text-cyan-300', icon: 'ðŸ’ ' };
    if (elo >= 1400) return { name: 'PLATINUM', color: 'text-emerald-300', icon: 'ðŸ”·' };
    if (elo >= 1200) return { name: 'GOLD', color: 'text-yellow-400', icon: 'ðŸŸ¨' };
    if (elo >= 1000) return { name: 'SILVER', color: 'text-gray-300', icon: 'â¬œ' };
    if (elo >= 800) return { name: 'BRONZE', color: 'text-orange-400', icon: 'ðŸŸ«' };
    return { name: 'IRON', color: 'text-gray-500', icon: 'â¬›' };
  };

  const getRankDisplay = (elo: number) => {
    const rank = getRankFromElo(elo);
    return (
      <div className="flex items-center">
        <span className="text-lg mr-2">{rank.icon}</span>
        <span className={`text-sm font-bold ${rank.color}`}>
          {rank.name}
        </span>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="max-w-4xl mx-auto">
        <div className="text-center py-12">
          <div className="spinner mx-auto mb-4"></div>
          <p className="text-valorant-gray-300">Loading leaderboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto">
      {/* Header */}
      <div className="text-center mb-8">
        <h1 className="text-4xl font-valorant text-white mb-4 text-glow">
          LEADERBOARD
        </h1>
        <p className="text-valorant-gray-300 text-lg">
          Top players ranked by ELO rating
        </p>
      </div>

      {/* Filter Tabs */}
      <div className="flex justify-center mb-8">
        <div className="bg-valorant-gray-800 p-1 rounded-lg inline-flex">
          {(['all', 'weekly', 'monthly'] as const).map((period) => (
            <button
              key={period}
              onClick={() => setFilter(period)}
              className={`
                px-6 py-2 rounded-md transition-all duration-200 font-medium capitalize
                ${filter === period 
                  ? 'bg-valorant-red text-white' 
                  : 'text-valorant-gray-300 hover:text-white hover:bg-valorant-gray-700'
                }
              `}
            >
              {period === 'all' ? 'All Time' : period}
            </button>
          ))}
        </div>
      </div>

      {/* Leaderboard */}
      <div className="bg-valorant-dark-secondary border border-valorant-red/20 rounded-lg overflow-hidden">
        {/* Header Row */}
        <div className="bg-valorant-dark border-b border-valorant-red/30 px-6 py-4">
          <div className="grid grid-cols-6 gap-4 text-sm font-valorant text-valorant-red uppercase tracking-wider">
            <div>RANK</div>
            <div className="col-span-2">PLAYER</div>
            <div>ELO</div>
            <div>W/L</div>
            <div>WIN RATE</div>
          </div>
        </div>

        {/* Player Rows */}
        <div className="divide-y divide-valorant-red/10">
          {leaderboard.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-valorant-gray-400">No leaderboard data available</p>
            </div>
          ) : (
            leaderboard.map((entry, index) => {
              const position = index + 1;
              const winRate = entry.wins && entry.losses 
                ? Math.round((entry.wins / (entry.wins + entry.losses)) * 100)
                : 0;

              return (
                <div 
                  key={entry.userId}
                  className={`
                    px-6 py-5 hover:bg-valorant-red/5 transition-all duration-200
                    ${position <= 3 ? 'bg-gradient-to-r from-valorant-red/10 to-transparent border-l-4 border-valorant-red' : ''}
                  `}
                >
                  <div className="grid grid-cols-6 gap-4 items-center">
                    {/* Rank */}
                    <div className="flex items-center">
                      <span className="text-2xl mr-2">
                        {getRankIcon(position)}
                      </span>
                      <span className="text-white font-bold">
                        {position <= 3 ? '' : `#${position}`}
                      </span>
                    </div>

                    {/* Player (col-span-2) */}
                    <div className="col-span-2 flex items-center">
                      <div className="w-12 h-12 rounded-full flex items-center justify-center text-white font-bold mr-4 bg-gradient-to-br from-valorant-red to-red-700">
                        {entry.username.charAt(0).toUpperCase()}
                      </div>
                      <div>
                        <div className="text-white font-bold text-lg">
                          {entry.username}
                        </div>
                        {getRankDisplay(entry.elo)}
                      </div>
                    </div>

                    {/* ELO */}
                    <div className="text-center">
                      <div className="text-valorant-red font-bold text-2xl font-valorant">
                        {entry.elo}
                      </div>
                      <div className="text-valorant-gray-400 text-xs uppercase tracking-wide">
                        Rating
                      </div>
                    </div>

                    {/* W/L */}
                    <div className="text-center">
                      <div className="flex items-center justify-center space-x-1">
                        <span className="text-green-400 font-bold">
                          {entry.wins || 0}
                        </span>
                        <span className="text-valorant-gray-500">/</span>
                        <span className="text-red-400 font-bold">
                          {entry.losses || 0}
                        </span>
                      </div>
                      <div className="text-valorant-gray-400 text-xs">
                        {(entry.wins || 0) + (entry.losses || 0)} games
                      </div>
                    </div>

                    {/* Win Rate */}
                    <div className="text-center">
                      <div className="text-white font-bold text-lg">
                        {winRate}%
                      </div>
                      <div className="w-full bg-valorant-dark rounded-full h-1.5 mt-1">
                        <div 
                          className="bg-gradient-to-r from-valorant-red to-red-400 h-1.5 rounded-full transition-all duration-500"
                          style={{ width: `${winRate}%` }}
                        />
                      </div>
                    </div>
                  </div>
                </div>
              );
            })
          )}
        </div>
      </div>

      {/* Stats Summary */}
      <div className="mt-8 grid md:grid-cols-3 gap-6">
        <div className="card text-center">
          <h3 className="text-lg font-semibold text-white mb-2">Total Players</h3>
          <div className="text-3xl font-bold text-valorant-red">
            {leaderboard.length}
          </div>
        </div>

        <div className="card text-center">
          <h3 className="text-lg font-semibold text-white mb-2">Average ELO</h3>
          <div className="text-3xl font-bold text-valorant-red">
            {leaderboard.length > 0 
              ? Math.round(leaderboard.reduce((sum, entry) => sum + entry.elo, 0) / leaderboard.length)
              : 0
            }
          </div>
        </div>

        <div className="card text-center">
          <h3 className="text-lg font-semibold text-white mb-2">Top Player</h3>
          <div className="text-xl font-bold text-white">
            {leaderboard.length > 0 ? leaderboard[0].username : 'N/A'}
          </div>
          <div className="text-valorant-gray-400">
            {leaderboard.length > 0 ? `${leaderboard[0].elo} ELO` : ''}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Leaderboard;
