import React, { useState, useEffect } from 'react';
import QueueStatus from './QueueStatus';
import MatchFound from './MatchFound';
import { QueueData, Match } from '../../types';

interface MatchmakingProps {}

const Matchmaking: React.FC<MatchmakingProps> = () => {
  const [isInQueue, setIsInQueue] = useState(false);
  const [queueData, setQueueData] = useState<QueueData>({
    players_in_queue: 0,
    players: [],
    estimated_wait: '2-5 minutes',
    can_start_match: false
  });
  const [currentMatch, setCurrentMatch] = useState<Match | null>(null);
  const [loading, setLoading] = useState(false);

  // Fetch queue status
  const fetchQueueStatus = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/queue/status');
      const data = await response.json();
      if (data.success) {
        setQueueData(data.data);
      }
    } catch (error) {
      console.error('Error fetching queue status:', error);
    }
  };

  // Join queue
  const joinQueue = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:8080/api/queue/join', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-User-ID': 'temp-user-id' // TODO: Get from auth context
        }
      });

      if (response.ok) {
        setIsInQueue(true);
        fetchQueueStatus();
      }
    } catch (error) {
      console.error('Error joining queue:', error);
    } finally {
      setLoading(false);
    }
  };

  // Leave queue
  const leaveQueue = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:8080/api/queue/leave', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'X-User-ID': 'temp-user-id' // TODO: Get from auth context
        }
      });

      if (response.ok) {
        setIsInQueue(false);
        fetchQueueStatus();
      }
    } catch (error) {
      console.error('Error leaving queue:', error);
    } finally {
      setLoading(false);
    }
  };

  // Start match (when enough players)
  const startMatch = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/match/start', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      const data = await response.json();
      if (data.success) {
        setCurrentMatch(data.data);
        setIsInQueue(false);
      }
    } catch (error) {
      console.error('Error starting match:', error);
    }
  };

  // Poll queue status when in queue
  useEffect(() => {
    fetchQueueStatus();

    const interval = setInterval(() => {
      if (isInQueue) {
        fetchQueueStatus();
      }
    }, 3000); // Update every 3 seconds

    return () => clearInterval(interval);
  }, [isInQueue]);

  // Auto-start match when enough players
  useEffect(() => {
    if (queueData.can_start_match && isInQueue) {
      // In a real app, this would be triggered by the server
      // For now, we'll manually trigger it
    }
  }, [queueData.can_start_match, isInQueue]);

  if (currentMatch) {
    return <MatchFound match={currentMatch} />;
  }

  return (
    <div className="max-w-4xl mx-auto">
      <div className="text-center mb-8">
        <h1 className="text-4xl font-valorant text-white mb-4 text-glow">
          MATCHMAKING
        </h1>
        <p className="text-valorant-gray-300 text-lg">
          Find players and compete in ranked matches
        </p>
      </div>

      <div className="grid md:grid-cols-2 gap-8">
        {/* Queue Controls */}
        <div className="card">
          <h2 className="text-2xl font-bold mb-6 text-valorant-red">Queue Controls</h2>

          {!isInQueue ? (
            <div className="space-y-4">
              <button
                onClick={joinQueue}
                disabled={loading}
                className="btn-primary w-full text-xl py-4 relative"
              >
                {loading ? (
                  <div className="flex items-center justify-center">
                    <div className="spinner mr-2"></div>
                    Joining...
                  </div>
                ) : (
                  'JOIN QUEUE'
                )}
              </button>

              <div className="text-center text-valorant-gray-400">
                <p>Click to join the matchmaking queue</p>
                <p className="text-sm mt-1">Match starts when 10 players are ready</p>
              </div>
            </div>
          ) : (
            <div className="space-y-4">
              <div className="text-center">
                <div className="inline-flex items-center bg-green-600 text-green-100 px-4 py-2 rounded-lg mb-4">
                  <div className="w-3 h-3 bg-green-300 rounded-full mr-2 animate-pulse"></div>
                  IN QUEUE
                </div>

                <p className="text-valorant-gray-300 mb-4">
                  Estimated wait time: <span className="text-white font-bold">{queueData.estimated_wait}</span>
                </p>
              </div>

              <button
                onClick={leaveQueue}
                disabled={loading}
                className="btn-secondary w-full"
              >
                {loading ? (
                  <div className="flex items-center justify-center">
                    <div className="spinner mr-2"></div>
                    Leaving...
                  </div>
                ) : (
                  'LEAVE QUEUE'
                )}
              </button>
            </div>
          )}
        </div>

        {/* Queue Status */}
        <QueueStatus queueData={queueData} isInQueue={isInQueue} />
      </div>

      {/* Quick Actions */}
      <div className="mt-8 text-center">
        <div className="inline-flex space-x-4">
          <button className="btn-outline">
            Practice Range
          </button>
          <button className="btn-outline">
            View Recent Matches
          </button>
        </div>
      </div>
    </div>
  );
};

export default Matchmaking;
