import { useState, useEffect } from 'react';
import { loginUser, registerUser, logoutUser } from '../services/auth';

const useAuth = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUser = async () => {
            // Logic to fetch the current user from the backend or local storage
            // setUser(fetchedUser);
            setLoading(false);
        };

        fetchUser();
    }, []);

    const login = async (credentials) => {
        setLoading(true);
        setError(null);
        try {
            const fetchedUser = await loginUser(credentials);
            setUser(fetchedUser);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const register = async (userData) => {
        setLoading(true);
        setError(null);
        try {
            const fetchedUser = await registerUser(userData);
            setUser(fetchedUser);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const logout = async () => {
        setLoading(true);
        setError(null);
        try {
            await logoutUser();
            setUser(null);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return { user, loading, error, login, register, logout };
};

export default useAuth;
